import React, { useState, useEffect } from 'react'
import Axios from 'axios'
import InsertData from './InsertData';
function ShowData() {
    const [isShow, setIsShow] = useState(false)
    const [movieData, setMovieData] = useState([])

    const [movieName, setMovieName] = useState()
    const [year, setYear] = useState()
    const [plot, setPlot] = useState()
    const editMovie = (Movie_id) => {
        Axios.put("http://localhost:3001/movie", {
            movieName: movieName,
            year: year,
            plot: plot,
            Movie_id: Movie_id
        }).then(
            console.log("Data updated")
        );

    }
    const deleteMovie = (Movie_id) => {
        Axios.delete(`http://localhost:3001/movies/${Movie_id}`);
    }
    useEffect(() => {
        Axios.get("http://localhost:3001/movies").then((response) => {

            setMovieData(response.data)
        })
    }, []);
    return (
        <div className="row mt-3">
            {movieData.map((val) => {
                return (

                    <div className="col-4">
                        {!isShow && <div className="border rounded-2 m-3 p-4">
                            <div className="mb-2">
                                <span className="fw-bolder">Name: </span>
                                <span>{val.Name}</span>
                            </div>

                            <div className="mb-2">
                                <span className="fw-bolder">Year of Release: </span>
                                <span>{val["Year of Release"]}</span>

                            </div>
                            <span className="fw-bolder">Plot: </span>
                            <span>{val.Plot}</span>
                            <hr />
                            <button className="btn btn-outline-primary me-3" onClick={() => setIsShow(true)}>Edit</button>
                            <button className="btn btn-outline-danger" onClick={() => { deleteMovie(val.Movie_id) }}>Delete</button>

                        </div>}
                        {isShow && <div className="border rounded-2 m-3 p-4">
                            <div className="mb-2">
                                <span className="fw-bolder">Name: </span>
                                <input className='form-control' type="text" defaultValue={val.Name} onChange={(e) => {
                                    setMovieName(e.target.value);

                                }

                                } />
                            </div>

                            <div className="mb-2">
                                <span className="fw-bolder">Year of Release: </span>
                                <input className='form-control' type="text" defaultValue={val['Year of Release']} onChange={(e) => {

                                        setYear(e.target.value)

                                }

                                } />

                            </div>
                            <span className="fw-bolder">Plot: </span>
                            <input className='form-control' type="text" defaultValue={val.Plot} onChange={(e) => {

                                        setPlot(e.target.value)

                                }

                                } />
                            <hr />
                            <button className="btn btn-outline-primary me-3" onClick={() => {

                            if(typemovieName==null){
                                setMovieName(val.Name)
                            }
                            if(year==null){
                                setYear(val['Year of Release'])
                            }
                            if(plot==null){
                                setPlot(val.Plot)
                            }
                             editMovie(val.Movie_id)
                            }}>Update</button>
                        </div>}


                    </div>

                )
            })}
        </div>
    )

}
export default ShowData
