import React, {Component} from 'react'
import Axios from 'axios'

class InsertData extends Component{
    constructor(){
        super();
        this.state={

        };


    }

     submitData=() => {

      Axios.post("http://localhost:3001/movie",{
        movieName : this.state.movieName,
        yor:this.state.yor,
        plot:this.state.plot,
        poster:this.state.poster
      }).then(
        console.log("data inserted")
      );

    };
    convertToBase64 = (file) => {
        return new Promise((resolve, reject) => {
          const fileReader = new FileReader();
          fileReader.readAsDataURL(file);
          fileReader.onload = () => {
            resolve(fileReader.result);
          };
          fileReader.onerror = (error) => {
            reject(error);
          };
        });
      };
    handleFileUpload= async (e) => {
        const file = e.target.files[0];
        const base64 = await convertToBase64(file);
        this.setState({
            poster:base64
        })
      };
render(){
    // console.log(this.state.moviename);
    return (
    <div className='form '>
        <form className='form'>
        <label htmlFor="mo_name">Movie Name</label>
        <input type="text" id="mo_name" className='form-control mt-3' required onChange={(e) => 
        this.setState({
            movieName:e.target.value
        })
        }/>
        <label htmlFor="mo_date">Year of Release</label>
        <input type="date" id="mo_date" className='form-control mt-3' required onChange={(e) => 
        this.setState({
            yor:e.target.value
        })
        }/>
        <label htmlFor="mo_plot">Plot</label>
        <input type="text" id="mo_plot" className='form-control mt-3' required onChange={(e) => 
        this.setState({
            plot:e.target.value
        })
        }/>
        <label htmlFor="mo_poster">Poster</label>
        <input type="file" id="mo_poster" className='form-control mt-3' required onChange={(e) => 
        handleFileUpload(e)
        }/>
        <button type="submit" onClick={() => this.submitData() } className='btn btn-primary  mt-3'>Submit</button>
        </form>
    </div>
    );
}


}
export default InsertData
