import React, { useState,useEffect } from 'react'
import Axios from 'axios'
function AddData() {
    const [movieName, setMovieName] = useState()
    const [year, setYear] = useState()
    const [plot, setPlot] = useState()

    const submitData = () => {

        Axios.post("http://localhost:3001/movie", {
            movieName: movieName,
            year: year,
            plot: plot,

        }).then(
            console.log("Data inserted")
        );


    }
   return (
    <div className='form m-5 w-25'>
        <form className='form'>
        <label htmlFor="mo_name" className='mt-2'>Movie Name</label>
        <input type="text" id="mo_name" className='form-control mt-3' required onChange={(e) => 
        setMovieName(e.target.value)        }/>

        <label htmlFor="mo_date" className='mt-2'>Actor</label>
        <input type="text" id="mo_date" className='form-control mt-3' required onChange={(e) => 
        setYear(e.target.value) 
        }/>
        <label htmlFor="mo_date" className='mt-2'>Year of Release</label>
        <input type="text" id="mo_date" className='form-control mt-3' required onChange={(e) => 
        setYear(e.target.value) 
        }/>
        <label htmlFor="mo_plot" className='mt-2'>Plot</label>
        <input type="text" id="mo_plot" className='form-control mt-3' required onChange={(e) => 
        setPlot(e.target.value) 
        }/>

        <button type="submit" onClick={() => submitData() } className='btn btn-primary  mt-3'>Submit</button>
        </form>
    </div>
   );



}
export default AddData
