import React, { useState, useEffect } from 'react'
import Axios from 'axios'
import InsertData from './InsertData';
function ShowData(){
const [isShow, setIsShow]=useState()
const [movieData, setMovieData] =useState([])
const [movie_id, setMovie_id] =useState([])
const editMovie=(Movie_id)=> {
    Axios.put(`http://localhost:3001/movie/${Movie_id}`).then((response)=>{

        setMovie_id(response.data)
    })
}
const deleteMovie= (Movie_id) =>{
    Axios.delete(`http://localhost:3001/movies/${Movie_id}`);
}
useEffect(()=>{
    Axios.get("http://localhost:3001/movies").then((response)=>{

        setMovieData(response.data)
    })
},[]);
    return (
        <div className="row mt-3">
            {movieData.map((val)=>{
                return(

                        <div className="col-4">
                            <div className="border rounded-2 m-3 p-4">
                                <div className="mb-2">
                                    <span className="fw-bolder">Name: </span>
                                    <span>{val.Name}</span>
                                </div>

                                  <div className="mb-2">
                                    <span className="fw-bolder">Year of Release: </span>
                                  <span>{val["Year of Release"]}</span>

                                  </div>
                                  <span className="fw-bolder">Plot: </span>
                                  <span>{val.Plot}</span>
                                  <hr/>
                                  <button className="btn btn-outline-primary me-3" onClick={()=> editMovie(1)}>Edit</button>
                                  <button className="btn btn-outline-danger" onClick={()=> {deleteMovie(val.Movie_id)}}>Delete</button>

                                </div>


                        </div>

            )
            })}
        </div>
    )

}
export default ShowData
